import { useState } from "react";

const CalculatorForm = () => {
  const [currentSavings, setCurrentSavings] = useState();
  const [yearlyContribution, setYearlyContribution] = useState();
  const [expectedReturn, setExpectedReturn] = useState();
  const [duration, setDuration] = useState();
  const updateValue = (target, value) => {
    if (target === "current-savings") {
      setCurrentSavings(value);
      console.log(value)
    } else if (target === "yearly-contribution") {
      setYearlyContribution(value);
    } else if (target === "expected-return") {
      setExpectedReturn(value);
    } else if (target === "duration") {
      setDuration(value);
    }
  };
  const calculateSubmit = (event) => {
    event.preventDefault();
    setCurrentSavings();
    setYearlyContribution();
    setExpectedReturn();
    setDuration();
  };

  return (
    <form className="form" onSubmit={calculateSubmit}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input type="number" id="current-savings"  onChange={(event)=>{updateValue('current-savings',event.target.value)}} />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            value={yearlyContribution}
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input type="number" id="expected-return" value={expectedReturn} />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" id="duration" value={duration} />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default CalculatorForm;
